

public class Block {
	int blockOperation;
	int blockId;
	int size;
	int offset;
	int src;
	int dest;


	public Block(int blockOperation, int blockId, int size, int offset, int src, int dest) {
		// TODO Auto-generated constructor stub
		this.blockOperation = blockOperation;
		this.blockId = blockId;
		this.size = size;
		this.offset = offset;
		this.src = src;
		this.dest = dest;
	}
}
